mapper.update:
UPDATE sys_course_application
template_id = #{templateId}, course_name = #{courseName}, teacher_id = #{teacherId}, teacher_name = #{teacherName}, academic_year = #{academicYear}, semester = #{semester}, max_students = #{maxStudents}, course_hours = #{courseHours}, remaining_hours = #{remainingHours}, reason = #{reason}, status = #{status}, apply_time = #{applyTime}, review_time = #{reviewTime}, reviewer_id = #{reviewerId}, reviewer_name = #{reviewerName}, review_comment = #{reviewComment}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
SELECT 
FROM sys_course_application

    deleted = 0

        AND course_name LIKE CONCAT('%', #{courseApplication.courseName}, '%')


        AND teacher_name LIKE CONCAT('%', #{courseApplication.teacherName}, '%')


        AND academic_year = #{courseApplication.academicYear}


        AND semester = #{courseApplication.semester}


        AND status = #{courseApplication.status}


ORDER BY apply_time DESC

SELECT 
FROM sys_course_application
WHERE teacher_id = #{teacherId} 
  AND academic_year = #{academicYear} 
  AND status = #{status} 
  AND deleted = 0
ORDER BY apply_time DESC

SELECT COUNT(*)
FROM sys_course_application

    deleted = 0

        AND course_name LIKE CONCAT('%', #{courseApplication.courseName}, '%')


        AND teacher_name LIKE CONCAT('%', #{courseApplication.teacherName}, '%')


        AND academic_year = #{courseApplication.academicYear}


        AND semester = #{courseApplication.semester}


        AND status = #{courseApplication.status}


        AND teacher_id = #{courseApplication.teacherId}
