mapper.update:
UPDATE sys_course_template
template_name = #{templateName}, description = #{description}, course_hours = #{courseHours}, academic_year = #{academicYear}, semester = #{semester}, max_students = #{maxStudents}, status = #{status}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
SELECT 
FROM sys_course_template

    deleted = 0

        AND template_name LIKE CONCAT('%', #{courseTemplate.templateName}, '%')


        AND academic_year = #{courseTemplate.academicYear}


        AND semester = #{courseTemplate.semester}


        AND status = #{courseTemplate.status}


ORDER BY create_time DESC

SELECT 
FROM sys_course_template

    deleted = 0

        AND (template_name LIKE CONCAT('%', #{keyword}, '%')
        OR description LIKE CONCAT('%', #{keyword}, '%'))


        AND academic_year = #{courseTemplate.academicYear}


        AND semester = #{courseTemplate.semester}


        AND status = #{courseTemplate.status}


ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM sys_course_template

    deleted = 0

        AND template_name LIKE CONCAT('%', #{courseTemplate.templateName}, '%')


        AND academic_year = #{courseTemplate.academicYear}


        AND semester = #{courseTemplate.semester}


        AND status = #{courseTemplate.status}
