mapper.update:
UPDATE sys_student
student_no = #{studentNo}, name = #{name}, gender = #{gender}, phone = #{phone}, email = #{email}, major = #{major}, class_name = #{className}, grade = #{grade}, education_system = #{educationSystem}, enrollment_date = #{enrollmentDate}, graduation_date = #{graduationDate}, current_year = #{currentYear}, current_semester = #{currentSemester}, current_academic_year = #{currentAcademicYear}, status = #{status}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
SELECT 
FROM sys_student

    deleted = 0

        AND student_no = #{student.studentNo}


        AND name = #{student.name}


        AND status = #{student.status}


ORDER BY create_time DESC

SELECT COUNT(*)
FROM sys_student

    deleted = 0

        AND student_no = #{student.studentNo}


        AND name = #{student.name}


        AND status = #{student.status}
